import React, { useState, useEffect } from "react";
import InventoryCard from "../Components/InventoryCard";
import axios from "axios";
import Moment from 'react-moment';

const Inventory = () => {
  const [data, setData] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [response, setResponse] = useState({});

  useEffect(() => {
    const jwtToken = localStorage.getItem("token");
    if (!jwtToken) {
      window.location.replace("/");
    }
    const fetchData = async () => {
      const res = await axios.get(`${process.env.REACT_APP_URL}/getInventory`, {
        headers: { Authorization: `JWT ${jwtToken}` },
      });
      setData(res.data);
    };

    fetchData();
    setInventory([... new Set(data.map((item)=>item.name))]);
  }, []);
  var moment = require('moment');


  return (
    <table className="table-fixed">
      <thead>
        <tr>
          <th>Name</th>
          <th>Qty</th>
          <th>Price</th>
          <th>Exp_date</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <InventoryCard
            name={item.name}
            qty={item.quantity}
            price={item.price_unit}
            exp_date={moment(item.exp_date).format('YYYY-MM-DD')}
            color = {moment(item.exp_date).format('YYYY-MM-DD')<moment().format('YYYY-MM-DD')?"red":"black"}
          />
        ))}
        {   console.log("name",inventory)}
      </tbody>
    </table>
  );
};
export default Inventory;
